import React, { useState } from "react";

const EmployeeCreate = ({ createEmployee }) => {
    const [formData, setFormData] = useState({
        firstName: "",
        lastName: "",
        age: "",
        dateOfJoining: "",
        title: "Employee",
        department: "IT",
        employeeType: "FullTime",
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        createEmployee({ variables: { ...formData, age: parseInt(formData.age) } });
    };

    return (
        <form onSubmit={handleSubmit}>
            <input name="firstName" placeholder="First Name" onChange={handleChange} required />
            <input name="lastName" placeholder="Last Name" onChange={handleChange} required />
            <input name="age" type="number" min="20" max="70" placeholder="Age" onChange={handleChange} required />
            <input name="dateOfJoining" type="date" onChange={handleChange} required />
            <select name="title" onChange={handleChange}>
                <option value="Employee">Employee</option>
                <option value="Manager">Manager</option>
                <option value="Director">Director</option>
                <option value="VP">VP</option>
            </select>
            <select name="department" onChange={handleChange}>
                <option value="IT">IT</option>
                <option value="Marketing">Marketing</option>
                <option value="HR">HR</option>
                <option value="Engineering">Engineering</option>
            </select>
            <select name="employeeType" onChange={handleChange}>
                <option value="FullTime">FullTime</option>
                <option value="PartTime">PartTime</option>
                <option value="Contract">Contract</option>
                <option value="Seasonal">Seasonal</option>
            </select>
            <button type="submit">Create Employee</button>
        </form>
    );
};

export default EmployeeCreate;
